// Data kommer nå som en kombinert liste fra Merge-noden
// Vi må identifisere kilde basert på datastruktur

const allItems = $input.all();

// Separer data basert på hvilke felter som finnes
const budgetData = [];
const actualData = [];
const prognosisData = [];

for (const item of allItems) {
  const json = item.json;

  // Identifiser kilde basert på kolonnenavn
  if (json['Budget Amount'] !== undefined || json.budget !== undefined) {
    budgetData.push(item);
  } else if (json['Actual Amount'] !== undefined || json.actual !== undefined) {
    actualData.push(item);
  } else if (json['Prognosis Amount'] !== undefined || json.prognosis !== undefined || json['Expected Revenue'] !== undefined) {
    prognosisData.push(item);
  }
}

console.log(`Budget rows: ${budgetData.length}, Actual rows: ${actualData.length}, Prognosis rows: ${prognosisData.length}`);

// Bygg lookup maps
const budgetMap = new Map();
budgetData.forEach(item => {
  const key = item.json.Account || item.json.account;
  if (key) {
    budgetMap.set(key, {
      budget: parseFloat(item.json['Budget Amount'] || item.json.budget || 0),
      period: item.json.Period || item.json.period || 'Unknown'
    });
  }
});

const actualMap = new Map();
actualData.forEach(item => {
  const key = item.json.Account || item.json.account;
  if (key) {
    actualMap.set(key, parseFloat(item.json['Actual Amount'] || item.json.actual || 0));
  }
});

const prognosisMap = new Map();
prognosisData.forEach(item => {
  const key = item.json.Account || item.json.account;
  if (key) {
    prognosisMap.set(key, parseFloat(item.json['Prognosis Amount'] || item.json.prognosis || item.json['Expected Revenue'] || 0));
  }
});

// Kombiner alle unike accounts
const allAccounts = new Set([
  ...budgetMap.keys(),
  ...actualMap.keys(),
  ...prognosisMap.keys()
]);

// Bygg combined output med variance-beregninger
const results = [];
for (const account of allAccounts) {
  const budgetInfo = budgetMap.get(account) || { budget: 0, period: 'Unknown' };
  const budget = budgetInfo.budget;
  const actual = actualMap.get(account) || 0;
  const prognosis = prognosisMap.get(account) || 0;

  // Beregn variances
  const varVsBudget = actual - budget;
  const varVsBudgetPct = budget !== 0 ? ((actual - budget) / budget) * 100 : 0;
  const varVsPrognosis = actual - prognosis;
  const varVsPrognosisPct = prognosis !== 0 ? ((actual - prognosis) / prognosis) * 100 : 0;

  results.push({
    json: {
      Account: account,
      Period: budgetInfo.period,
      Budget: budget,
      Prognosis: prognosis,
      Actual: actual,
      'Var vs Budget': varVsBudget,
      'Var vs Budget %': Math.round(varVsBudgetPct * 10) / 10,
      'Var vs Prognosis': varVsPrognosis,
      'Var vs Prognosis %': Math.round(varVsPrognosisPct * 10) / 10,
      'Updated': new Date().toISOString()
    }
  });
}

// Sorter etter Account
results.sort((a, b) => a.json.Account.localeCompare(b.json.Account));

if (results.length === 0) {
  return [{ json: { error: 'No data found in any source' } }];
}

return results;
