// Transform Odoo opportunities to match expected format
const opportunities = $input.all();
const results = [];

for (const opp of opportunities) {
  const json = opp.json;

  // Get date_deadline for period calculation
  const deadline = json.date_deadline;
  let period = 'Unknown';

  if (deadline) {
    const date = new Date(deadline);
    period = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;
  }

  // Get account name from partner_name or partner_id array
  const account = json.partner_name || 
                  (Array.isArray(json.partner_id) ? json.partner_id[1] : null);

  if (!account) {
    console.log('Skipping opportunity without account:', json.name);
    continue;
  }

  // Check if Won stage
  const stageInfo = json.stage_id;
  const stageName = Array.isArray(stageInfo) ? stageInfo[1] : (stageInfo || '');
  const isWon = stageName.toLowerCase().includes('won') || 
                json.won_status === 'won';

  // Check if Lost (we exclude these)
  const isLost = stageName.toLowerCase().includes('lost') || 
                 json.won_status === 'lost' ||
                 json.active === false;

  if (isLost) {
    console.log('Skipping lost opportunity:', json.name);
    continue;
  }

  results.push({
    json: {
      Account: account,
      Period: period,
      Prognosis: parseFloat(json.prorated_revenue) || 0,
      OpportunityName: json.name || json.display_name,
      IsWon: isWon,
      Stage: stageName,
      SourceType: 'odoo'
    }
  });
}

console.log(`Transformed ${results.length} opportunities from Odoo`);
return results;

// Merge & Calculate Variances with Monthly Breakdown and Won/Pipeline Split
const allItems = $input.all();

// Separate data by source type
const budgetData = [];
const actualData = [];
const prognosisData = [];

for (const item of allItems) {
  const json = item.json;

  // Odoo data has SourceType = 'odoo'
  if (json.SourceType === 'odoo') {
    prognosisData.push(item);
  }
  // Budget data has 'Budget Name' field
  else if (json['Budget Name'] !== undefined && typeof json.Budget === 'number') {
    budgetData.push(item);
  }
  // Actual data has 'Actual' field
  else if (json.Actual !== undefined) {
    actualData.push(item);
  }
}

console.log(`Budget: ${budgetData.length}, Actual: ${actualData.length}, Prognosis: ${prognosisData.length}`);

// Build aggregation maps with Account + Period as key
// Budget Map: { 'Account|Period': { budget: number, period: string } }
const budgetMap = new Map();
budgetData.forEach(item => {
  const account = item.json.Account;
  // Convert period to YYYY-MM format if needed
  let period = String(item.json.Period || 'Unknown');
  if (/^\d{4}$/.test(period)) {
    // If just year, assume January
    period = `${period}-01`;
  }

  const key = `${account}|${period}`;
  const existing = budgetMap.get(key) || { budget: 0 };
  budgetMap.set(key, {
    budget: existing.budget + (parseFloat(item.json.Budget) || 0),
    account,
    period
  });
});

// Actual Map: { 'Account|Period': { actual: number } }
const actualMap = new Map();
actualData.forEach(item => {
  const account = item.json.Account;
  let period = String(item.json.Period || 'Unknown');
  if (/^\d{4}$/.test(period)) {
    period = `${period}-01`;
  }

  const key = `${account}|${period}`;
  const existing = actualMap.get(key) || { actual: 0 };
  actualMap.set(key, {
    actual: existing.actual + (parseFloat(item.json.Actual) || 0),
    account,
    period
  });
});

// Prognosis Map with Won/Pipeline split
// { 'Account|Period': { won: number, pipeline: number, total: number } }
const prognosisMap = new Map();
prognosisData.forEach(item => {
  const account = item.json.Account;
  const period = item.json.Period || 'Unknown';
  const amount = parseFloat(item.json.Prognosis) || 0;
  const isWon = item.json.IsWon === true;

  const key = `${account}|${period}`;
  const existing = prognosisMap.get(key) || { won: 0, pipeline: 0, total: 0 };

  if (isWon) {
    existing.won += amount;
  } else {
    existing.pipeline += amount;
  }
  existing.total = existing.won + existing.pipeline;
  existing.account = account;
  existing.period = period;

  prognosisMap.set(key, existing);
});

// Collect all unique Account|Period combinations
const allKeys = new Set([
  ...budgetMap.keys(),
  ...actualMap.keys(),
  ...prognosisMap.keys()
]);

// Build results
const results = [];
for (const key of allKeys) {
  const budgetInfo = budgetMap.get(key);
  const actualInfo = actualMap.get(key);
  const progInfo = prognosisMap.get(key);

  // Extract account and period from the key
  const [account, period] = key.split('|');

  const budget = budgetInfo?.budget || 0;
  const actual = actualInfo?.actual || 0;
  const won = progInfo?.won || 0;
  const pipeline = progInfo?.pipeline || 0;
  const prognosis = won + pipeline;

  // Calculate variances
  const varVsBudget = actual - budget;
  const varVsBudgetPct = budget !== 0 ? Math.round(((actual - budget) / budget) * 1000) / 10 : 0;
  const varVsPrognosis = actual - prognosis;
  const varVsPrognosisPct = prognosis !== 0 ? Math.round(((actual - prognosis) / prognosis) * 1000) / 10 : 0;

  results.push({
    json: {
      Account: account,
      Period: period,
      Budget: budget,
      Won: won,
      Pipeline: pipeline,
      Prognosis: prognosis,
      Actual: actual,
      'Var vs Budget': varVsBudget,
      'Var vs Budget %': varVsBudgetPct,
      'Var vs Prognosis': varVsPrognosis,
      'Var vs Prognosis %': varVsPrognosisPct,
      Updated: new Date().toISOString()
    }
  });
}

// Sort by Account, then Period
results.sort((a, b) => {
  const accountCmp = a.json.Account.localeCompare(b.json.Account);
  if (accountCmp !== 0) return accountCmp;
  return a.json.Period.localeCompare(b.json.Period);
});

if (results.length === 0) {
  return [{ json: { error: 'No data found in any source' } }];
}

console.log(`Generated ${results.length} dashboard rows`);
return results;
